@layer base {
  :root {
    --background: 220 20% 10%;
    --foreground: 30 10% 95%;

    --card: 220 18% 14%;
    --card-foreground: 30 10% 95%;

    --popover: 220 18% 14%;
    --popover-foreground: 30 10% 95%;

    --primary: 16 85% 60%;
    --primary-foreground: 220 20% 10%;

    --secondary: 220 15% 20%;
    --secondary-foreground: 30 10% 95%;

    --muted: 220 15% 18%;
    --muted-foreground: 220 10% 55%;

    --accent: 16 70% 50%;
    --accent-foreground: 30 10% 95%;

    --destructive: 0 72% 51%;
    --destructive-foreground: 30 10% 95%;

    --success: 142 70% 45%;
    --success-foreground: 220 20% 10%;

    --warning: 38 92% 50%;
    --warning-foreground: 220 20% 10%;

    --border: 220 15% 22%;
    --input: 220 15% 22%;
    --ring: 16 85% 60%;

    --radius: 0.75rem;

    --shadow-glow: 0 0 30px hsl(16 85% 60% / 0.3);
    --shadow-card: 0 4px 20px hsl(0 0% 0% / 0.3);
  }
}

@layer utilities {
  .animate-pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  .animate-heartbeat {
    animation: heartbeat 1.5s ease-in-out infinite;
  }

  .animate-fade-in {
    animation: fade-in 0.5s ease-out;
  }

  .animate-slide-up {
    animation: slide-up 0.5s ease-out;
  }

  .gradient-radial {
    background: radial-gradient(circle at center, hsl(var(--primary) / 0.15) 0%, transparent 70%);
  }
}

@keyframes pulse-glow {
  0%, 100% {
    box-shadow: 0 0 20px hsl(var(--primary) / 0.4), 0 0 40px hsl(var(--primary) / 0.2);
  }
  50% {
    box-shadow: 0 0 40px hsl(var(--primary) / 0.6), 0 0 80px hsl(var(--primary) / 0.3);
  }
}

@keyframes heartbeat {
  0%, 100% { transform: scale(1); }
  14% { transform: scale(1.1); }
  28% { transform: scale(1); }
  42% { transform: scale(1.1); }
  70% { transform: scale(1); }
}

@keyframes fade-in {
  0% { opacity: 0; }
  100% { opacity: 1; }
}

@keyframes slide-up {
  0% { opacity: 0; transform: translateY(20px); }
  100% { opacity: 1; transform: translateY(0); }
}

body {
  background-color: hsl(var(--background));
  color: hsl(var(--foreground));
  min-height: 100vh;
}

